// Generated by CoffeeScript 1.6.3
$(document).ready(function() {
  return $('table.blog .del, table.blog .undel').click(function(event) {
    var $post, data;
    event.preventDefault();
    data = {
      'meta.hide': $(this).hasClass('del')
    };
    $.ajax({
      url: '/admin/api/pages/' + $(this).data('id'),
      type: 'POST',
      data: data
    });
    $post = $(this).parents('tr');
    $post.find('.del, .undel').toggleClass('hide');
    return $post.toggleClass('disabled');
  });
});
